package Withourusingthirdvariable;

public class Maximumproduct {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result =0;
	    int []arr={3,4,7,3,4,5,6};
	    for(int i:arr)
	    {
	        result ^=i;
	    }

	    System.out.println("Result is "+result);
	}
	}


